content.code_example:
// Declaring variables
let myName = 'Alice';
const myAge = 25;
var isStudent = true;

console.log(myName); // Outputs: Alice
console.log(myAge); // Outputs: 25
console.log(isStudent); // Outputs: true

// Different data types
let greeting = 'Hello, World!'; // String
let count = 10; // Number
let isActive = true; // Boolean
let user = {name: 'Alice', age: 25}; // Object
let numbers = [1, 2, 3, 4]; // Array

console.log(greeting);
console.log(count);
console.log(isActive);
console.log(user);
console.log(numbers);

// If statement example
let score = 85;
if (score >= 60) {
    console.log('You passed!');
} else {
    console.log('You failed.');
}

// For loop example
for (let i = 1; i <= 5; i++) {
    console.log('Count: ' + i);
}

// Function to greet someone
function greet(name) {
    return 'Hello, ' + name + '!';
}

let greetingMessage = greet('Alice');
console.log(greetingMessage); // Outputs: Hello, Alice!
